import React, { useEffect, useState } from 'react';
import { getAllRooms } from '../utils/ApiFunctions';
import RoomPaginator from '../common/RoomPaginator';
import AddRoom from './AddRoom';

const ExistingRoom = () => {
    console.log("ExistingRoom component is mounting");
    const [rooms, setRooms] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [roomsPerPage] = useState(8);
    const [isLoading, setIsLoading] = useState(false);
    const [filteredRooms, setFilteredRooms] = useState([]);
    const [errorMessage, setErrorMessage] = useState("");
    const [successMessage, setSuccessMessage] = useState("");
    const [filterText, setFilterText] = useState(""); // New state for filter text

    useEffect(() => {
        console.log("useEffect triggered");
        fetchRoom();
    }, []);
    
    useEffect(() => {
        if (rooms.length > 0) {
            applyFilter(); // Apply filter whenever rooms are fetched or changed
            console.log("Rooms state updated:", rooms);
        }
    }, [rooms, filterText]);

    const fetchRoom = async () => {
        console.log("fetchRoom is being called"); 
        setIsLoading(true);
        try {
            const result = await getAllRooms(); // Ensure this function makes the API call
            setRooms(result); // Update state with the fetched rooms
            setIsLoading(false);
            setSuccessMessage(""); // Clear any previous success messages
            console.log("Fetched rooms:", result); // Add this for debugging
        } catch (error) {
            setErrorMessage("Error fetching rooms");
            console.error("Error fetching rooms:", error); // Add this for debugging
        }
    };

    const applyFilter = () => {
        if (filterText.trim() === "") {
            setFilteredRooms(rooms);
        } else {
            const lowercasedFilterText = filterText.toLowerCase();
            const filtered = rooms.filter(room =>
                room.roomType.toLowerCase().includes(lowercasedFilterText) ||
                room.roomPrice.toString().includes(lowercasedFilterText)
            );
            setFilteredRooms(filtered);
        }
    };

    const handleRoomAdded = (newRoom) => {
        setRooms((prevRooms) => [newRoom, ...prevRooms]);
        setSuccessMessage("Room added successfully!");
        setErrorMessage(""); // Clear any previous error messages
    };

    const handlePaginationClick = (pageNumber) => {
        setCurrentPage(pageNumber);
    };

    const calculateTotalPages = () => {
        const totalRooms = filteredRooms.length > 0 ? filteredRooms.length : rooms.length;
        return Math.ceil(totalRooms / roomsPerPage);
    };

    const indexOfLastRoom = currentPage * roomsPerPage;
    const indexOfFirstRoom = indexOfLastRoom - roomsPerPage;
    const currentRooms = filteredRooms.slice(indexOfFirstRoom, indexOfLastRoom);

    console.log(currentRooms);

    return (
        <>
            {isLoading ? (
                <p>Loading Existing Rooms</p>
            ) : (
                <>
                    <section className='mb-5 mt-5 container'>
                        <div className="d-flex justify-content-center mb-3 mt-5">
                            <h2>Existing Rooms</h2>
                        </div>
                        <AddRoom onRoomAdded={handleRoomAdded} />
                        <div className="mb-3">
                            <input
                                type="text"
                                className="form-control"
                                placeholder="Filter by room type or price"
                                value={filterText}
                                onChange={(e) => {
                                    setFilterText(e.target.value);
                                }}
                            />
                        </div>
                        {successMessage && <div className="alert alert-success">{successMessage}</div>}
                        {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}
                        <div className="table-responsive">
                            <table className='table table-bordered table-hover'>
                                <thead>
                                    <tr className='text-center'>
                                        <th>ID</th>
                                        <th>Room Type</th>
                                        <th>Room Price</th>
                                        <th>Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {currentRooms.map((room) => (
                                        <tr key={room.id} className='text-center'>
                                            <td>{room.id}</td>
                                            <td>{room.roomType}</td>
                                            <td>{room.roomPrice}</td>
                                            <td>
                                                <button>View / Edit</button>
                                                <button>Delete</button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                        <RoomPaginator 
                            currentPage={currentPage}
                            totalPages={calculateTotalPages()}
                            onPageChange={handlePaginationClick}
                        />
                    </section>
                </>
            )}
        </>
    );
};

export default ExistingRoom;
