import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import 'bootstrap/dist/css/bootstrap.min.css';  // Ensure Bootstrap is imported
import "./navbar.css";

const Navbar = () => {
    const { isAuthenticated, user, logout } = useAuth();  
    const navigate = useNavigate();
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);  

    const handleLogout = () => {
        logout();  
        navigate('/login');  
    };

    const toggleDropdown = () => {
        setIsDropdownOpen(!isDropdownOpen);  
    };

    return (
        <nav className="navbar navbar-expand-lg navbar-dark bg-primary">
            <div className="container">
                <span className="navbar-brand">Website Name</span>
                <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav ml-auto">
                        {!isAuthenticated ? (
                            <>
                                <li className="nav-item">
                                    <Link to="/register" className="navButton nav-link">Register</Link>
                                </li>
                                <li className="nav-item">
                                    <Link to="/login" className="navButton nav-link">Login</Link>
                                </li>
                            </>
                        ) : (
                            <li className="nav-item dropdown">
                                <div className="d-flex align-items-center">
                                    <div className="userCircle" onClick={toggleDropdown}>
                                        {user && user.firstname ? user.firstname.charAt(0).toUpperCase() : 'U'}
                                    </div>
                                    <span className="userName mx-2">{user ? user.firstname : ''}</span>
                                    {isDropdownOpen && (
                                        <div className="dropdown-menu show">
                                            <span className="dropdown-item">{user ? user.firstname : ''}</span>
                                            <button className="dropdown-item navButton" onClick={handleLogout}>Logout</button>
                                        </div>
                                    )}
                                </div>
                            </li>
                        )}
                    </ul>
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
