import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import "bootstrap/dist/css/bootstrap.min.css"; // Import Bootstrap CSS
import "./navbar.css"; // Import custom CSS if needed

const Navbar = () => {
    const { isAuthenticated, user, logout } = useAuth();  // Destructure auth state, user info, and logout function
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();  // Update authentication state
        navigate('/login');  // Redirect to login page
    };

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container">
                <span className="navbar-brand">Website Name</span>
                <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <div className="navbar-nav ml-auto">
                        {!isAuthenticated ? (
                            <>
                                <Link to="/register" className="nav-item nav-link"><button className="btn btn-primary">Register</button></Link>
                                <Link to="/login" className="nav-item nav-link"><button className="btn btn-primary">Login</button></Link>
                            </>
                        ) : (
                            <div className="d-flex align-items-center">
                                <div className="userProfile d-flex align-items-center">
                                    <button className="btn btn-outline-primary ml-2" onClick={handleLogout}>Logout</button>
                                    <div className="userInfo d-flex align-items-center ml-2">
                                        <div className="userCircle">
                                            {/* Display user initials or a default value if no user name */}
                                            {user && user.name ? user.name.charAt(0).toUpperCase() : 'U'}
                                        </div>
                                        <span className="userName ml-2">{user ? user.name : 'User'}</span>
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
